import { getByPlaceholderText } from '@testing-library/react';
import React, {useState} from 'react';
import ReactDOM from 'react-dom';
import {useForm} from 'react-hook-form';
import placeholder from './placeholder.jpg'
function App(){
  return (
  <Navbar>

    <NavItem text = "Machines" icon = "🔽">
      <DropdownMenu></DropdownMenu>
    </NavItem>


  </Navbar>


  );
}

function DropdownMenu(){


   function DropdownItem(props){
    return(
      <a href = "##" className = "menu-item" onClick={() => ReactDOM.render(props.form, document.getElementById("form"))} >
        <span className = "icon-button" >{props.text}</span>
        {props.children}
      </a>

    );
  }


  return (
    <div className = "dropdown">
      <DropdownItem text = "H-111 Front" form = {MachineForm( "H-111 Front")} ></DropdownItem>
      <DropdownItem text = "H-111 Rear"></DropdownItem>
      <DropdownItem text = "H-111 Re-Built"></DropdownItem>
      <DropdownItem text = "FHB-106"></DropdownItem>
      <DropdownItem text = "O54"></DropdownItem>
      <DropdownItem text = "H-121"></DropdownItem>
      <DropdownItem text = "BA-15"></DropdownItem>
    </div>
  );

}

function Navbar(props){
  return(
    <nav className = "navbar">
      <ul className = "navbar-nav">{props.children}</ul>
    </nav>
  );
}

function NavItem(props){
  const [open, setOpen] = useState(false);
  return(
      <li className = 'navitem'>
        <a href = "#" className = "icon-button" onClick = { () => setOpen(!open)}>
          {props.text + props.icon}
        </a>
        {open && props.children}
      </li>
  );
}

function MachineForm(text){
  const {register, handleSubmit} = useForm();

  const onSubmit = (data) => {
    alert(data);
    console.log(data);
  }

  return(
    // <div>
    //   <h1>{text}</h1><br/><br/>
    //   <img src = {placeholder}/>  
      <form onSubmit = "handleSubmit(onSubmit)">
        {/* <label>Part 1</label> */}
        <input type = "checkbox" id = "part1" name = "part1" value = "Part 1"></input><br/>
        {/* <label>Part 2</label> */}
        <input type = "checkbox" id = "part2" name = "part2" value = "Part 2"></input><br/>
        {/* <label>Part 3</label> */}
        <input type = "checkbox" id = "part3" name = "part3" value = "Part 3"></input><br/>
        {/* <label>Part 4</label> */}
        <input type = "checkbox" id = "part4" name = "part4" value = "Part 4"></input><br/>
        {/* <label>Part 5</label> */}
        <input type = "checkbox" id = "part5" name = "part5" value = "Part 5"></input><br/>

        {/* <label>Date Maintained</label><br/> */}
        <input type = "date" id = "date_maintained" name = "date_maintained"></input><br/>

        {/* <label>Notes</label><br/> */}
        <input type = "text" name = "notes" ></input><br/>

        <input type = "submit"></input>
      </form>
    // </div>


  );
}


export default App;
